<template>
  <view class="job_detail">
     <u-navbar class="app_navbar" height="56px" title="Company Details" :title-bold="true" :placeholder="true"
        leftIconColor="#00328f">
        <view slot="left" class="nav_title" >
          <u-icon name="arrow-left" @click="goBack" color="#00328f" size="22" ></u-icon>
        </view>
      </u-navbar>
    <view v-if="loading"  class="skeleton_detail">
      <u-skeleton style="margin-bottom: 40rpx;" rows="4" title loading v-for="item in 10" :key="item"></u-skeleton>
    </view>
    <view v-if="!loading">
      <view class="job_detail_content">
        <view class="job_top">
          <image :src="detailInfo.logo || defaultAvatar" class="logo"  mode="aspectFill" lazy-load="false"></image>
          <view>
            <view class="name">{{detailInfo.companyname}}</view>
            <view>{{detailInfo.hr_name}}</view>
          </view>
          
        </view>
        <view class="content_info">
          <view class="p"><u-icon name="home" color="#00328f" size="20"></u-icon> :{{detailInfo.address}}</view>
          <view class="p"><u-icon name="share" color="#00328f" size="20"></u-icon> : <a :href="detailInfo.googlema">{{detailInfo.googlemap}}</a></view>
        
          <view class="content_text">
            {{detailInfo.content}}
          </view>
          <view>
            <image :src="item.url " class="img" v-for="(item,i) in detailInfo.images" :key="i" mode="aspectFill" lazy-load="false"></image>
          </view>
        </view>
      </view> 
    </view>
  </view>
</template>

<script>
import { getCompanyInfo } from '@/api/resume'
import imConfig from "@/common/config.js"; // 本地配置数据
export default {
  data() {
    return {
      params: {},
      detailInfo:{},
			defaultAvatar: 'data:image/jpg;base64,',

      loading:true,
    }
  },
  onLoad(query) {
    this.params.id = query.id
    this.getDetail()
    
  },
  methods: {
    getDetail() {
      getCompanyInfo(this.params).then(res => {
        this.detailInfo = res
        
        this.loading = false
      })
    },
    goBack(params) {
      const pages = getCurrentPages()
					if (pages.length === 1) {
						if (typeof params === 'number') {
							history.go(-params)
						} else {
							history.back()
						}
					} else {
						uni.navigateBack()
					}
    },
    
  }
}
</script>

